(function($) {
    var secure = [];
	$('iframe').each(function(i) {
        var type;

        if($(this).attr('src').indexOf('youtube')) {
            type = 'YouTube';
        }

        secure[i] = {
            type: type,
            width: $(this).attr('width'),
            height: $(this).attr('height'),
            src: $(this).attr('src')
        };

        $(this).attr('src','').attr('height', 0).attr('width', 0);
        $('<button href="#" id="test">Hiermit aktzeptiere ich den Datenschutz.</button>').insertAfter($(this));
    });

    $('#test').live('click', function(e) {
        e.preventDefault();
        $('iframe').each(function(i){
            $(this).attr('src', secure[i]['src']).attr('width', secure[i]['width']).attr('height', secure[i]['height']);
        });
    });

    console.log(secure);
})( jQuery );